import React, { useEffect } from 'react';
import ReactDOM from 'react-dom'
import ModalStyle from './Modal.module.css';
const Modal = (props) => {
    let container;
    useEffect(() => {
        if (typeof window !== 'undefined') {
            const rootContainer = document.createElement('div');
            const parentElem = document.querySelector('#__next');
            parentElem.appendChild(rootContainer);
            container = rootContainer;
          }
        document.getElementById('body__modal_buscador').classList.add('show');
        document.getElementsByTagName('body')[0].style.overflowY = 'hidden';
    }, [])
    const closeModal = ()=>{
        document.getElementById('body__modal_buscador').classList.remove('show');
        setTimeout(() => {
            document.getElementsByTagName('body')[0].style.overflowY = 'scroll';
            return props.closeModal();
        }, 500);
    }
    return (
        ReactDOM.createPortal(
            <div className={ModalStyle.Modal}>
                <div className={ModalStyle.modal_dialog} id="body__modal_buscador">
                    <div className={ModalStyle.modal_content}>
                        {props.children}
                        <i onClick={closeModal} className={ModalStyle.close_modal + ' ' + `fas fa-times`} style={{cursor:'pointer'}}></i>
                    </div>
                </div>
            </div>,
            container
        )
    );
}

export default Modal;
