import Head from '../../src/components/Head'
import React from 'react';
import BannerUsuario from '../../src/components/BannerUsuario';
import TabsNav from '../../src/components/Tabs/navs';
import TabsContent from '../../src/components/Tabs/content';
import FormEditUsuario from '../../src/components/FormEditUsuario';

const Perfil = () => {
    const [activeTab, setActiveTab] = React.useState('1');

    const toggle = tab => {
        if(activeTab !== tab) setActiveTab(tab);
    }

    const solapas = [
        {
            nombre:'General'
        },
        {
            nombre:'Mis compras'
        }
    ];

    return (
        <>
            <Head title='Oliver Petshop - Mi perfil'/>
            <div className="container">
                <BannerUsuario/>
                <TabsNav toggle={toggle} activeTab={activeTab} solapas={solapas}/>
            </div>
            <div className="container-form">
                <div className="container pt-3">
                    <TabsContent activeTab={activeTab} solapas={solapas} formUsuario={FormEditUsuario}/>
                </div>
            </div>

            <style jsx>{`
                .container-form{
                    background-color:#fff;
                    height:65vh;
                }    
            `}</style>
        </>
    );
}
 
export default Perfil;
