import { faPencilAlt } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { useState } from "react";

const FormEditUsuario = () => {
    const [formDisabled, setFormDisabled] = useState({
        nombre:true,
        telefono:true,
        email:true,
        address:true
    });

    const habilitarInput = nameInput=>{
        return setFormDisabled({
            ...formDisabled,
            [nameInput]:false
        })
    }

    return (
        <form className="form-group">
            <div className="row">
                <div className="col-12 col-md-6 my-4 d-flex align-items-center">
                    <input disabled={formDisabled.nombre} type="text" name="nombre" id="nombre" className="form-control mr-auto"/>
                    <FontAwesomeIcon icon={faPencilAlt} onClick={()=>habilitarInput('nombre')}/>
                </div>
                <div className="col-12 col-md-6 my-4 d-flex align-items-center">
                    <input disabled={formDisabled.telefono} type="number" name="telefono" id="telefono" className="form-control mr-auto"/>
                    <FontAwesomeIcon icon={faPencilAlt} onClick={()=>habilitarInput('telefono')}/>
                </div>
                <div className="col-12 col-md-6 my-4 d-flex align-items-center">
                    <input disabled={formDisabled.email} type="email" name="email" id="email" className="form-control mr-auto"/>
                    <FontAwesomeIcon icon={faPencilAlt} onClick={()=>habilitarInput('email')}/>
                </div>
                <div className="col-12 col-md-6 my-4 d-flex align-items-center">
                    <input disabled={formDisabled.address} type="text" name="address" id="address" className="form-control mr-auto"/>
                    <FontAwesomeIcon icon={faPencilAlt} onClick={()=>habilitarInput('address')}/>
                </div>
            </div>
            <style jsx>{`
                .form-control{
                    width:92%
                }    
            `}</style>
        </form>
    );
}
 
export default FormEditUsuario;
