import React from 'react';
const DetalleProductos = () => {
    return (
        <div className="containerDetalleProducto">
            <div className="infoProducto text-center">
                <img src="https://storage.googleapis.com/web-oliver/-VitalCrops-DogPro-Adulto-15k.jpg" alt="prd"/>
                <h3 className="my-0">Dog Pro Adulto</h3>
                <span className="text-muted">Categoria: <b>Perro</b></span>
                <span>Cantidad: 1</span>
            </div>
            <div className="infoPago">
                <hr/>
                <span className="d-flex justify-content-between precioProducto">
                    <p>Producto:</p>
                    <span>$850</span>
                </span>
                <hr/>
                <span className="d-flex justify-content-between precioTotal" style={{fontWeight:'bold'}}>
                    <p>Pag√°s</p>
                    <span>$850</span>
                </span>
            </div>
            <style jsx>{`
                .containerDetalleProducto{
                    position:fixed;
                    height:100vh;
                    background-color:white;
                    width:20%;
                    font-family: 'Quicksand', sans-serif;
                }

                .infoProducto h3{
                    font-size:15px;
                    font-weight: 300;
                    color: #333;
                    margin-bottom: 8px;
                    padding: 0 40px;  
                }
                .infoProducto span{
                    color: #333;
                    display: block;
                    font-size: 14px;
                }

                .infoProducto img{
                    width:65px;
                    height:80px;
                }

                .infoPago{
                    padding:5px 15px
                }

                .infoPago .precioProducto{
                    font-size: 16px;
                    font-weight: 300;
                    color: #333;
                    padding-bottom: 10px;
                }

                .infoPago .precioTotal{
                    font-size: 18px;
                    line-height: 18px;
                }
            `}</style>
        </div>
    );
}
 
export default DetalleProductos;
