import {SELECCIONAR, TRAER_ZONAS,LOADING,ERROR} from '../types/zonasTypes'

const INITIAL_STATE = {
    zonas:[],
    zona:null,
    loading:false,
    error:null
}

const zonasReducer = (state=INITIAL_STATE,action)=>{
    switch (action.type) {
        case TRAER_ZONAS:
            return {
                ...state,
                zonas:action.payload,
                loading:false,
                error:null,
                zona:null
            }
        case SELECCIONAR:
            return {
                ...state,
                zona:action.payload,
                loading:false,
                error:null
            }
        case LOADING:
            return {...state,loading:true}
        case ERROR:
            return {...state,loading:false,error:action.payload}
        default:
            return state;
    }
}

export default zonasReducer;
