import React,{useState} from 'react';
import { TabContent, TabPane, Nav, NavItem, NavLink, Card, Button, CardTitle, CardText, Row, Col } from 'reactstrap';
import classnames from 'classnames';
import InfoStyle from './infoProducto.module.css'
const InfoProducto = (props) => {
    const [activeTab, setActiveTab] = useState('1');

    const toggle = tab => {
        if(activeTab !== tab) setActiveTab(tab);
    }

    return (
        <>
            <section className={InfoStyle.detalles__product}>
                <Nav tabs>
                    <NavItem>
                        <NavLink
                            className={classnames({ active: activeTab === '1' }) + ' ' + InfoStyle.nav__item}
                            onClick={() => { toggle('1'); }}
                        >
                            Descripción
                        </NavLink>
                    </NavItem>
                    <NavItem>
                        <NavLink
                            className={classnames({ active: activeTab === '2' })+ ' ' + InfoStyle.nav__item}
                            onClick={() => { toggle('2'); }}
                        >
                            Información nutricional
                        </NavLink>
                    </NavItem>
                </Nav>
                <TabContent activeTab={activeTab}>
                    <TabPane tabId="1">
                    <Row>
                        <Col sm="12">
                            {props.descripcion_basica}
                        </Col>
                    </Row>
                    </TabPane>
                    <TabPane tabId="2">
                    <Row>
                        <Col sm="12">
                            {props.descripcion}
                        </Col>
                    </Row>
                    </TabPane>
                </TabContent>
            </section>
        </>
    );
}
 
export default InfoProducto;
