import {API} from '../../config/index';
import {TRAER_INFO,LOGIN,LOGOUT,LOADING,ERROR} from '../types/usuarioTypes';

export const login = (data)=>async(dispatch)=>{
    dispatch({
        type:LOADING
    });
    try {
        if(data.email.trim() === '' || data.password.trim() === ''){
            return dispatch({
                type:ERROR,
                payload:'Es necesario completar todos los campos'
            })
        }
        let headers = new Headers();
        headers.append("Content-Type", "application/json");
        return fetch(`${API}/login`,{
            method:'POST',
            body:JSON.stringify(data),
            headers
        }).then(res=>res.json()).then(response=>{
            if(response.ok){
                localStorage.setItem('usuario',JSON.stringify(response.usuario));
                dispatch({
                    type:LOGIN,
                    payload:response.usuario
                })
            }
        })
    } catch (error) {
        dispatch({
            type:ERROR,
            payload:error
        })
    }
}

export const logout = ()=>async(dispatch)=>{
    dispatch({
        type:LOGIN
    });
    try {
        localStorage.removeItem('usuario');
        return dispatch({
            type:LOGOUT
        })
    } catch (error) {
        dispatch({
            type:ERROR,
            payload:error
        })
    }
}

export const traerInfo=()=>async(dispatch)=>{
    try {
        const {usuario} = getState().usuarioReducer;
        if(usuario){
            return getState().usuarioReducer;
        }
        let dataUsuario = localStorage.getItem('usuario');
        dispatch({
            type:TRAER_INFO,
            payload:JSON.parse(dataUsuario)
        })
    } catch (error) {
        dispatch({
            type:ERROR,
            payload:error
        })
    }
}
