import {connect} from 'react-redux';
import *  as usuarioActions from '../../../store/actions/usuarioActions';

const FormModificarPw = (props) => {

    const sendEmail = ()=>{
        const {idUsuario} = props.usuarioReducer.usuario;
        props.sendEmailForResetPassword(idUsuario);
    };

    console.log(props);
    return (
        <div className="containerResetPass">
            <div className="alert alert-info text-center">Estaremos enviando un email con los pasos para poder realizar el proceso de modificación de contraseña</div>
            <div className="col-12 text-center">
                <button onClick={sendEmail} type="button" className="boton bg-yellow">Enviar email</button>
            </div>
            <style jsx>{`
                .containerResetPass{
                    font-family: 'Quicksand', sans-serif!important;
                }
            `}</style>
        </div>
    );
}

const mapStateToProps = ({usuarioReducer})=>{
    return {usuarioReducer}
}

export default connect(mapStateToProps,usuarioActions)(FormModificarPw);
