import Head from '../../src/components/Head'
import React from 'react';
import BannerUsuario from '../../src/components/BannerUsuario';
import TabsNav from '../../src/components/Tabs/navs';
import FormEditUsuario from '../../src/components/FormEditUsuario';

const Perfil = () => {
    const [activeTab, setActiveTab] = React.useState('1');

    const toggle = tab => {
        if(activeTab !== tab) setActiveTab(tab);
    }

    const solapas = [
        {
            nombre:'General'
        },
        {
            nombre:'Mis compras'
        }
    ];

    return (
        <>
            <Head title='Oliver Petshop - Mi perfil'/>
            <div className="container">
                <BannerUsuario/>
                <TabsNav toggle={toggle} activeTab={activeTab} solapas={solapas}/>
            </div>
            <div className="container-form">
                <div className="container pt-3">
                    <TabContent activeTab={activeTab} className={InfoStyle.sub__container}>
                        <TabPane tabId="1">
                            <Row>
                                <Col sm="12">
                                    <FormEditUsuario/>
                                </Col>
                            </Row>
                        </TabPane>
                        <TabPane tabId="2">
                            <Row>
                                <Col sm="12">
                                    <div className="alert alert-warning text-center">No hay ventas registradas</div>
                                </Col>
                            </Row>
                        </TabPane>
                    </TabContent>
                </div>
            </div>

            <style jsx>{`
                .container-form{
                    background-color:#fff;
                    height:65vh;
                }    
            `}</style>
        </>
    );
}
 
export default Perfil;
