import { useEffect, useState } from "react";
import Header from "../../src/components/Head";

const Venta = (props) => {
    const [formValues, setFormValues] = useState({
        email:'',
        docType:'',
        docNumber:'',
        cardTitular:'',
        cardNumber:'',
        cvc:'',
        vencimiento:'',
        bancoEmisor:'',
        cuotas:'',
        total:'',
        metodoPago:'',
        descripcion:''
    });

    const [vencimientosValues, setVencimientosValues] = useState({
        cardExpirationMonth:'',
        cardExpirationYear:''
    })

    useEffect(function mount() {
        window.Mercadopago.setPublishableKey("TEST-1f5f55a0-b128-49c4-9217-110a8d9fba43");
        window.Mercadopago.getIdentificationTypes();
    });

    const handleChange = e=>{
        setFormValues({
            ...formValues,
            [e.target.name]:e.target.value
        })
    }

    const handleChangeVencimiento = e=>{
        setVencimientosValues({
            ...vencimientosValues,
            [e.target.id]:e.target.value
        })
    };

    const guessPaymentMethod = (event)=>{
        let cardnumber = document.getElementById("cardNumber").value;
        if (cardnumber.length >= 6) {
            let bin = cardnumber.substring(0,6);
            window.Mercadopago.getPaymentMethod({
                "bin": bin
            }, setPaymentMethod);
        }
     };

    const setPaymentMethod = (status, response)=>{
        if (status == 200) {
            let paymentMethod = response[0];
            document.getElementById('paymentMethodId').value = paymentMethod.id;

            getIssuers(paymentMethod.id);
        } else {
            alert(`payment method info error: ${response}`);
        }
    }

    const getIssuers = (paymentMethodId)=>{
        window.Mercadopago.getIssuers(
            paymentMethodId,
            setIssuers
        );
     }

     const setIssuers = (status, response)=>{
        if (status == 200) {
            let issuerSelect = document.getElementById('issuer');
            response.forEach( issuer => {
                let opt = document.createElement('option');
                opt.text = issuer.name;
                opt.value = issuer.id;
                issuerSelect.appendChild(opt);
            });

            getInstallments(
                document.getElementById('paymentMethodId').value,
                document.getElementById('transactionAmount').value,
                issuerSelect.value
            );
        } else {
            alert(`issuers method info error: ${response}`);
        }
    }

    const getInstallments = (paymentMethodId, transactionAmount, issuerId)=>{
        window.Mercadopago.getInstallments({
            "payment_method_id": paymentMethodId,
            "amount": parseFloat(transactionAmount),
            "issuer_id": parseInt(issuerId)
        }, setInstallments);
     }

     const setInstallments = (status, response)=>{
        if (status == 200) {
            document.getElementById('installments').options.length = 0;
            response[0].payer_costs.forEach( payerCost => {
                let opt = document.createElement('option');
                opt.text = payerCost.recommended_message;
                opt.value = payerCost.installments;
                document.getElementById('installments').appendChild(opt);
            });
        } else {
            alert(`installments method info error: ${response}`);
        }
     }

    return (
        <div className="px-5">
            <Header title="Oliver PetShop"/>
            <form>
                <h3>Detalles del comprador</h3>
                <div>
                    <div>
                        <label htmlFor="email">E-mail</label>
                        <input id="email" name="email" type="text" value={formValues.email} onChange={handleChange}/>
                    </div>
                    <div>
                        <label htmlFor="docType">Tipo de documento</label>
                        <select id="docType" name="docType" data-checkout="docType" type="text" value={formValues.docType} onChange={handleChange}>
                        </select>
                    </div>
                    <div>
                        <label htmlFor="docNumber">Número de documento</label>
                        <input id="docNumber" name="docNumber" data-checkout="docNumber" value={formValues.docNumber} type="text" onChange={handleChange}/>
                    </div>
                </div>
                <h3>Detalles de la tarjeta</h3>
                <div>
                    <div>
                        <label htmlFor="cardholderName">Titular de la tarjeta</label>
                        <input id="cardholderName" data-checkout="cardholderName" type="text"/>
                    </div>
                    <div>
                        <label htmlFor="">Fecha de vencimiento</label>
                        <div>
                            <input type="text" placeholder="MM" id="cardExpirationMonth" data-checkout="cardExpirationMonth"
                                onselectstart="return false" onPaste={()=>false}
                                onCopy={()=>false} onCut={()=>false}
                                onDrag={()=>false} onDrop={()=>false} autoComplete="off" handleChange={handleChangeVencimiento} value={vencimientosValues.cardExpirationMonth}/>
                            <span className="date-separator">/</span>
                            <input type="text" placeholder="YY" id="cardExpirationYear" data-checkout="cardExpirationYear"
                                oonselectstart="return false" onPaste={()=>false}
                                onCopy={()=>false} onCut={()=>false}
                                onDrag={()=>false} onDrop={()=>false} autoComplete="off" handleChange={handleChangeVencimiento} value={vencimientosValues.cardExpirationYear}/>
                        </div>
                    </div>
                    <div>
                        <label htmlFor="cardNumber">Número de la tarjeta</label>
                        <input type="text" id="cardNumber" data-checkout="cardNumber"
                        onselectstart="return false" onPaste={()=>false}
                        onCopy={()=>false} onCut={()=>false}
                        onDrag={()=>false} onDrop={()=>false} autoComplete="off" onChange={guessPaymentMethod}/>
                    </div>
                    <div>
                        <label htmlFor="securityCode">Código de seguridad</label>
                        <input id="securityCode" data-checkout="securityCode" type="text"
                        onselectstart="return false" onPaste={()=>false}
                        onCopy={()=>false} onCut={()=>false}
                        onDrag={()=>false} onDrop={()=>false} autoComplete="off"/>
                    </div>
                    <div id="issuerInput">
                        <label htmlFor="issuer">Banco emisor</label>
                        <select id="issuer" name="issuer" data-checkout="issuer"></select>
                    </div>
                    <div>
                        <label htmlFor="installments">Cuotas</label>
                        <select type="text" id="installments" name="installments"></select>
                    </div>
                    <div>
                        <input type="hidden" name="transactionAmount" id="transactionAmount" value="100" />
                        <input type="hidden" name="paymentMethodId" id="paymentMethodId" />
                        <input type="hidden" name="description" id="description" />
                        <br/>
                        <button type="submit">Pagar</button>
                        <br/>
                    </div>
                </div>
            </form>
        </div>
    );
}

export default Venta;
