import React from 'react';
const DetalleProductos = () => {
    return (
        <div className="containerDetalleProducto">
            <div className="infoProducto text-center">
                {/* <img src="https://storage.googleapis.com/web-oliver/-VitalCrops-DogPro-Adulto-15k.jpg" alt="prd"/>
                <h3 className="my-2">Dog Pro Adulto</h3>
                <span>Cantidad: 1</span> */}
            </div>
            <div className="infoPago mt-5">
                <span className="txt-resumen mb-3">Resumen de compra</span>
                <hr className="mt-0"/>
                <span className="d-flex justify-content-between precioProducto">
                    <p>Producto:</p>
                    <span>$850</span>
                </span>
                <hr className="mt-0"/>
                <span className="d-flex justify-content-between precioTotal" style={{fontWeight:'bold'}}>
                    <p>Pagás</p>
                    <span>$850</span>
                </span>
            </div>
            <style jsx>{`
                .containerDetalleProducto{
                    position:fixed;
                    height:100vh; 
                    background-color:white;
                    width:20%;
                    font-family: 'Quicksand', sans-serif;
                    padding:50px 0px 0px 0px;
                }

                .infoProducto h3{
                    font-size:15px;
                    font-weight: 300;
                    color: #333;
                    margin-bottom: 8px;
                    padding: 0 40px;  
                }
                .infoProducto span{
                    color: #333;
                    display: block;
                    font-size: 14px;
                }

                .infoPago .txt-resumen{
                    font-size: 16px;
                    line-height: 16px;
                    font-weight: 600;
                    color: #333;
                    padding: 8px 0 12px;
                }

                .infoProducto img{
                    width:65px;
                    height:80px;
                }

                .infoPago{
                    padding:5px 15px
                }

                .infoPago .precioProducto{
                    font-size: 16px;
                    font-weight: 300;
                    color: #333;
                }

                .infoPago .precioTotal{
                    font-size: 18px;
                    line-height: 18px;
                }
            `}</style>
        </div>
    );
}
 
export default DetalleProductos;
