import React,{useEffect} from 'react';
import { Route } from "react-router-dom";
import {connect} from 'react-redux';
import * as productosActions from '../../../store/actions/productosActions';
const {restablecerFiltros} = productosActions;

const RutaComun = ({component:Component,...props}) => {
    useEffect(() => {
        if(props.productosReducer.filtrando){
            props.productosReducer.restablecerFiltros();
        }
    }, [])

    return (
        <Route
            {...props}
            render={<Component {...props}/>}
        />
    );
}


const mapDispatchToProps = {
    restablecerFiltros
}

const mapStateToProps = ({productosReducer})=>{productosReducer};

export default connect(mapStateToProps,mapDispatchToProps)(RutaComun);
