import React from 'react';
import InfoStyle from  './infoProducto.module.css';
const InfoProducto = (props) => {
    const changePage = nameItem=>{
        let items = document.getElementsByClassName(InfoStyle.itemMenuDetalleProducto);
        for (let index = 0; index < items.length; index++) {
            items[index].classList.remove(InfoStyle.active);
        }
        for (let index = 0; index < document.getElementsByClassName(InfoStyle.info_txt).length; index++) {
            document.getElementsByClassName(InfoStyle.info_txt)[index].classList.add('d-none');
        }
        document.getElementsByName(nameItem)[0].classList.add(InfoStyle.active);
        document.getElementById(nameItem).classList.remove('d-none');
    }
    return (
        <>
            <section className={InfoStyle.detalles__producto}>
                <header className={InfoStyle.header__detalles__producto}>
                    <div className="container">
                        <ul className={InfoStyle.listaMenu__detalles__producto}>
                            <li name="descripcion" onClick={()=>changePage('descripcion')} className={InfoStyle.itemMenuDetalleProducto+' '+InfoStyle.active}>Descripcion</li>
                            <li name="info-nutricional" onClick={()=>changePage('info-nutricional')} className={InfoStyle.itemMenuDetalleProducto}>Informaci√≥n nutricional</li>
                        </ul>
                    </div>
                </header>
            </section>
            <div className="container">
                <article className={InfoStyle.sub__container + ' ' + InfoStyle.info__detalle__producto}>
                    <span id="descripcion" className={InfoStyle.info_txt}>
                        <p>{props.descripcion_basica}</p>
                    </span>
                    <span id="info-nutricional" className={`d-none` + ' ' + InfoStyle.info_txt}>
                        <h2>Ingredientes</h2>
                        <p>{props.descripcion}</p> 
                    </span>
                </article>
            </div>
        </>
    );
}
 
export default InfoProducto;
